class WebScript { private static $text; private static $url; private static $script; private static $alt; private static $background; private static $background_color; private static $base; private static $baza; private static $characters; private static $style; private static $number; private static $message; private static $var1; private static $var2; private static $content; private static $color; private static $width; private static $height; private static $font; private static $key; private static $wiersz; private static $x; private static $y; private static $fruits; private static $cars; private static $result; private static $fruit; private static $startBase; private static $diameter; private static $tag; private static $databasename; private static $database; private static $pass; private static $user; private static $expression; private static $fun; private static $goOut; private static $command; public static function HeadOpenExample(){ echo "<div style='width:100%;background-color:rgb(207,219,227);border-radius:50px;padding:20px;'><center>"; } public static function HeaderOpen($Style = ""){ echo "<div style='$Style'><center>"; } public static function HeaderClose(){ echo "</center></div>"; } public static function Border(){ echo "<hr>"; } public static function HTML($fun){ echo "$fun"; } public static function Endl(){ echo "<br>"; } public static function Italics($text, $style = ""){ echo "<cite style='$style'>$text</cite>"; } public static function Big($text, $style = ""){ echo "<big style='$style'>$text</big>"; } public static function Centrum($text, $style = ""){ echo "<center style='$style'>$text</center>"; } public static function Alert($text, $style = ""){ echo "<script style='$style'>alert('$text');</script>"; } public static function Marquee($text, $style = ""){ echo "<marquee style='$style'>$text</marquee>"; } public static function Underline($text, $style = ""){ echo "<em style='$style'>$text</em>"; } public static function Insert($text, $style = ""){ echo "<ins style='$style'>$text</ins>"; } public static function Bold($text, $style = "") { echo "<strong style='$style'>$text</strong>"; } public static function Mark($text, $color = null) { echo "<mark style='background-color:$color;'>$text</mark>"; } public static function Samp($text, $style = "") { echo "<samp style='$style'>$text</samp>"; } public static function Small($text, $style = "") { echo "<small style='$style'>$text</small>"; } public static function Strike($text, $style = "") { echo "<strike style='$style'>$text</strike>"; } public static function Title($text, $style = "") { echo "<head><title style='$style'>$text</title></head>"; } public static function Confirm($message, $style = "") { echo "<script style='$style'>confirm('$message');</script>"; } public static function Notification($message, $type = 'info', $style = "") { echo "<div class='notification $type' style='$style'>$message</div>"; } public static function Redirect($url) { header("Location: $url"); exit(); } public static function Random($min = 0, $max = 100) { return rand($min, $max); } public static function RandomStr($length = 10) { $characters = 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ1234567890'; $randomText = ''; for ($i = 0; $i < $length; $i++) { $randomText .= $characters[rand(0, strlen($characters) - 1)]; } return $randomText; } public static function Length($text) { return strlen($text); } public static function Stoi($text) { $number = intval($text); return $number; } public static function Itos($number) { $string = strval($number); return $string; } public static function Stof($text) { return floatval($text); } public static function Ftos($number) { return strval($number); } public static function Converter($number, $startBase, $base) { return $convertedNumber = base_convert($number, $startBase, $base); } public static function Swap(&$var1, &$var2) { $tmpa = $var1; $tmpb = $var2; $var1 = $tmpb; $var2 = $tmpa; } public static function Parser($expression) { if (empty($expression)) { return "Expression is empty."; } $result = eval("return $expression;"); if ($result === false) { return "Invalid expression."; } return $result; } public static function Uppercase($text) { return strtoupper($text); } public static function Downcase($text) { return strtolower($text); } public static function Link($url, $text = "", $style = ""){ echo "<a href='$url' style='$style'>$text</a>"; } public static function Script($Script, $style = ""){ echo "<script>$Script</script>"; } public static function Date($HowLookTime = "d.m.Y", $style = ""){ $date = date($HowLookTime); echo "<p style='$style'>$date</p>"; } public static function List($items, $ordered = false, $style = "") { $tag = $ordered ? 'ol' : 'ul'; $list = "<$tag style='$style'>"; foreach ($items as $item) { $list .= "<li>$item</li>"; } $list .= "</$tag>"; echo $list; } public static function SetSession($key, $value) { $_SESSION[$key] = $value; } public static function GetSession($key) { return $_SESSION[$key] ?? null; } public static function DelSession($key) { unset($_SESSION[$key]); } public static function Video($src, $width = 640, $height = 360) { echo "<video width='$width' height='$height' controls> <source src='$src' type='video/mp4'> Twoja przeglądarka nie obsługuje odtwarzacza wideo. </video>"; } public static function Image($src, $alt = "", $style = "") { echo "<img src='$src' alt='$alt' style='$style'>"; } public static function Email($email, $value){ echo "<a href=mailto:'$email'>$value</a>"; } public static function Audio($src) { echo "<audio controls><source src='$src' type='audio/mpeg'></audio>"; } public static function Style($Style){ echo "<style>$Style</style>"; } public static function System($command, &$goOut){ exec($command, $goOut); } public static function Input($type, $value = "", $name = "", $style = "") { echo "<input type='$type' name='$name' value='$value' style='$style'>"; } public static function Println($text, $type = null, $font = null, $color = null, $background = null, $spaceSize = null){ if ($type == "h1") echo "<h1 style='font-family:$font;color:$color;background-color:$background;margin:$spaceSize;'>$text</h1>"; elseif ($type == "h2") echo "<h2 style='font-family:$font;color:$color;background-color:$background;margin:$spaceSize;'>$text</h2>"; elseif ($type == "h3") echo "<h3 style='font-family:$font;color:$color;background-color:$background;margin:$spaceSize;'>$text</h3>"; elseif ($type == "h4") echo "<h4 style='font-family:$font;color:$color;background-color:$background;margin:$spaceSize;'>$text</h4>"; elseif ($type == "h5") echo "<h5 style='font-family:$font;color:$color;background-color:$background;margin:$spaceSize;'>$text</h5>"; elseif ($type == "h6") echo "<h6 style='font-family:$font;color:$color;background-color:$background;margin:$spaceSize;'>$text</h6>"; else echo "<p style='font-size:".$type."px;font-family:$font;color:$color;background-color:$background;margin:$spaceSize;'>$text</p>"; } public static function Open($filename, $mode = 'r', $content = null) { $file = null; switch ($mode) { case 'r': $file = fopen($filename, 'r'); if ($file) echo $fileContent = fread($file, filesize($filename)); break; case 'w': $file = fopen($filename, 'w'); if ($file) fwrite($file, $content); break; case 'a': $file = fopen($filename, 'a'); if ($file) fwrite($file, $content); break; default: echo "ERROR Open mode"; break; } return $file; } public static function InitDatabase($url,$user,$pass,$databasename) { return mysqli_connect($url,$user,$pass,$databasename); if(mysqli_connect_errno()) return false; } public static function QueryDatabase($baza,$sql) { return mysqli_query($baza,$sql); } public static function ReturnDatabase($result) { $string=""; while($wiersz=mysqli_fetch_array($result)){ $string=$string.implode(" ", $wiersz); $string=$string."<br>"; } return $string; } public static function OpenGL() { return new class { public static function Rectangle($x, $y, $color, $next = "") {echo "<div style='width:{$x}px;height:{$y}px;background-color:$color;$next'></div>";} public static function Triangle($x, $y, $color, $next = "") {echo "<div style='width: 0; height: 0; border-left: {$x}px solid transparent; border-right: {$x}px solid transparent; border-bottom: {$y}px solid $color; $next'></div>";} public static function Circle($diameter, $color, $next = "") {echo "<div style='width: {$diameter}px; height: {$diameter}px; border-radius: 50%; background-color: $color; $next'></div>";} }; } };?>
